import React, { useEffect, useState } from 'react';
import api from '../services/api';
import StatusBadge from '../components/StatusBadge';

const Users = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Đổi sang route /admins nếu backend dùng model Admin
    api.get('/admins').then(res => setUsers(res.data));
  }, []);

  return (
    <div>
      <h2>Quản trị viên</h2>
      <table>
        <thead>
          <tr>
            <th>Email</th>
            <th>Tên hiển thị</th>
            <th>Vai trò</th>
            <th>Trạng thái</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user._id}>
              <td>{user.email}</td>
              <td>{user.full_name || user.displayName || user.username}</td>
              <td>{user.role}</td>
              <td>
                <StatusBadge status={user.active !== false ? 'active' : 'inactive'} />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Users;
