import React, { useEffect, useState } from 'react';
import api from '../services/api';
import StatusBadge from '../components/StatusBadge';

const Users = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    api.get('/users').then(res => setUsers(res.data));
  }, []);

  // Lọc chỉ những user có vai trò là admin/quản trị viên
  const admins = users.filter(user => user.role === 'admin' || user.role === 'superadmin' || user.role === 'quản trị viên');

  return (
    <div>
      <h2>Quản trị viên</h2>
      <table>
        <thead>
          <tr>
            <th>Email</th>
            <th>Tên hiển thị</th>
            <th>Vai trò</th>
            <th>Trạng thái</th>
          </tr>
        </thead>
        <tbody>
          {admins.map(user => (
            <tr key={user._id}>
              <td>{user.email}</td>
              <td>{user.displayName || user.full_name || user.username}</td>
              <td>{user.role}</td>
              <td>
                <StatusBadge status={user.active !== false ? 'active' : 'inactive'} />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {admins.length === 0 && <div>Không có quản trị viên nào.</div>}
    </div>
  );
};

export default Users;
