import React from 'react';
import ChartCard from '../components/ChartCard';
import MapView from '../components/MapView';
import { useRealtimeParking } from '../hooks/useRealtimeParking';

const Dashboard = () => {
  const { parkingLots } = useRealtimeParking();
  const totalLots = parkingLots.length;
  const totalAvailableSlots = parkingLots.reduce((sum, lot) => sum + (lot.availableSlots || 0), 0);

  return (
    <div>
      <h2>Dashboard</h2>
      {/* Thống kê nhanh */}
      <div style={{ display: 'flex', gap: 16, marginBottom: 24 }}>
        {/* Có thể lấy số liệu từ context hoặc API */}
        <ChartCard title="Tổng số bãi đỗ">
          <div>{totalLots}</div>
        </ChartCard>
        <ChartCard title="Số chỗ trống còn lại">
          <div>{totalAvailableSlots}</div>
        </ChartCard>
        <ChartCard title="Xe đang gửi">
          <div>...</div>
        </ChartCard>
        <ChartCard title="Thiết bị online">
          <div>...</div>
        </ChartCard>
        <ChartCard title="Doanh thu hôm nay">
          <div>...</div>
        </ChartCard>
      </div>
      {/* Biểu đồ */}
      <div style={{ display: 'flex', gap: 16 }}>
        <ChartCard title="Lượt xe vào/ra (ngày)">
          {/* Biểu đồ lượt xe vào/ra */}
          <div>Biểu đồ lượt xe vào/ra</div>
        </ChartCard>
        <ChartCard title="Doanh thu (ngày)">
          {/* Biểu đồ doanh thu */}
          <div>Biểu đồ doanh thu</div>
        </ChartCard>
      </div>
      {/* Bản đồ */}
      <div style={{ marginTop: 32 }}>
        <MapView />
      </div>
      {/* Có thể bổ sung bảng xe vào/ra gần đây, thông báo mới nhất, shortcut... */}
    </div>
  );
};

export default Dashboard;
